import styled, { css } from 'styled-components';

export const PostContainer = styled.div`
    background-color: #ffffff;
    border-radius: 5px;
    box-shadow: 0px 0px 10px 0px rgba(0,0,0,0.1);
    margin: 10px;
    padding: 10px;
    width: 100%;
`;
