import styled, { css } from 'styled-components';

export const MainContainer = styled.div`
    background-color: var(--mainContainer);
    border-radius: 15px;
    box-shadow: 0px 0px 10px 0px rgba(0,0,0,0.1);
    margin-top: 15px;
    padding-left: 10px;
    padding-right: 10px;
    padding-bottom: 10px;
    width: 95%;
    display: flex;
    flex-direction: column;
    align-items: center;
    color: var(--textInContainer);
`;
